import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import {
  AppBar,
  Toolbar,
  Container,
  Typography,
  Button,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Box,
  CssBaseline,
  CircularProgress,
  FormControlLabel,
  Checkbox,
  Table,
  TableBody,
  TableCell,
  TableRow,
  TableContainer,
  Paper as MuiPaper,
  Dialog,
  IconButton,
  GlobalStyles
} from '@mui/material';
import { createTheme, ThemeProvider } from '@mui/material/styles';

function CustomCloseIcon(props) {
  return (
    <svg
      {...props}
      viewBox="0 0 24 24"
      width="24"
      height="24"
      fill="currentColor"
      aria-label="Close dialog"
      style={{ transition: 'transform 0.3s' }}
      onMouseEnter={(e) => (e.currentTarget.style.transform = 'rotate(90deg)')}
      onMouseLeave={(e) => (e.currentTarget.style.transform = 'rotate(0deg)')}
    >
      <path d="M19,6.41L17.59,5L12,10.59L6.41,5L5,6.41L10.59,12L5,17.59L6.41,19L12,13.41L17.59,19L19,17.59L13.41,12Z" />
    </svg>
  );
}

CustomCloseIcon.propTypes = {
  style: PropTypes.object
};

function RenderMetrics({ metrics }) {
  // Define order for classification and regression metrics.
  const classificationOrder = [
    'accuracy', 'precision', 'recall', 'f1_score',
    'confusion_matrix', 'classification_report', 'feature_importances'
  ];
  const regressionOrder = [
    'mse', 'r2_score', 'mean_absolute_error',
    'coefficients', 'tree_plot', 'feature_importances'
  ];

  const isClassification = metrics.hasOwnProperty('accuracy');
  let orderedKeys = [];
  if (isClassification) {
    for (let key of classificationOrder) {
      if (metrics.hasOwnProperty(key)) {
        orderedKeys.push(key);
      }
    }
    // Add any extra keys not in the classificationOrder
    for (let key in metrics) {
      if (!orderedKeys.includes(key)) {
        orderedKeys.push(key);
      }
    }
  } else {
    for (let key of regressionOrder) {
      if (metrics.hasOwnProperty(key)) {
        orderedKeys.push(key);
      }
    }
    // Add any extra keys not in the regressionOrder
    for (let key in metrics) {
      if (!orderedKeys.includes(key)) {
        orderedKeys.push(key);
      }
    }
  }

  return (
    <Box
      sx={{
        mt: 3,
        p: 2,
        backgroundColor: 'rgba(255,255,255,0.9)',
        borderRadius: '8px',
        boxShadow: '0px 2px 8px rgba(0,0,0,0.15)',
        animation: 'fadeIn 1s ease-out'
      }}
    >
      <Typography
        variant="h6"
        sx={{
          color: '#1976d2',
          mb: 2,
          fontWeight: 'bold',
          textAlign: 'center',
          borderBottom: '2px solid #1976d2',
          pb: 0.5
        }}
      >
        Metrics
      </Typography>
      {orderedKeys.map((key) => {
        const value = metrics[key];
        // Display confusion matrix as a table
        if (key === 'confusion_matrix' && Array.isArray(value)) {
          return (
            <Box key={key} sx={{ mb: 3, animation: 'scaleUp 0.5s ease-out' }}>
              <Typography variant="subtitle2" sx={{ fontWeight: 'bold', color: '#333', mb: 1 }}>
                Confusion Matrix:
              </Typography>
              <TableContainer component={MuiPaper} sx={{ maxWidth: 400, mx: 'auto' }}>
                <Table size="small">
                  <TableBody>
                    {value.map((row, rowIndex) => (
                      <TableRow key={rowIndex}>
                        {row.map((cell, cellIndex) => (
                          <TableCell
                            key={cellIndex}
                            sx={{
                              border: '1px solid #ddd',
                              textAlign: 'center',
                              padding: '6px',
                              transition: 'background-color 0.3s, transform 0.3s',
                              '&:hover': { backgroundColor: '#f0f0f0', transform: 'scale(1.05)' }
                            }}
                          >
                            {cell}
                          </TableCell>
                        ))}
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </TableContainer>
            </Box>
          );
        }
        // Display classification report in a pre block
        else if (key === 'classification_report') {
          return (
            <Box key={key} sx={{ mb: 3, animation: 'fadeIn 0.8s ease-out' }}>
              <Typography variant="subtitle2" sx={{ fontWeight: 'bold', color: '#333', mb: 1 }}>
                Classification Report:
              </Typography>
              <Box
                sx={{
                  backgroundColor: '#eef6fc',
                  p: 2,
                  borderRadius: '4px',
                  overflowX: 'auto'
                }}
              >
                <Typography
                  variant="body2"
                  component="pre"
                  sx={{
                    m: 0,
                    whiteSpace: 'pre',
                    fontFamily: 'monospace',
                    fontSize: '0.9rem'
                  }}
                >
                  {value}
                </Typography>
              </Box>
            </Box>
          );
        }
        // For all other keys, display as text
        else {
          return (
            <Typography
              key={key}
              variant="body2"
              sx={{
                mb: 2,
                backgroundColor: '#fff',
                p: 1,
                borderRadius: '4px',
                boxShadow: 'inset 0 0 4px rgba(0,0,0,0.1)',
                fontWeight: 500,
                transition: 'transform 0.3s',
                '&:hover': { transform: 'translateX(5px)' }
              }}
            >
              <span style={{ fontWeight: 'bold', color: '#333' }}>
                {`${key.charAt(0).toUpperCase() + key.slice(1)}:`}
              </span>{' '}
              {Array.isArray(value) ? JSON.stringify(value) : value}
            </Typography>
          );
        }
      })}
    </Box>
  );
}

RenderMetrics.propTypes = {
  metrics: PropTypes.object.isRequired
};

function App() {
  const [file, setFile] = useState(null);
  const [algorithm, setAlgorithm] = useState('');
  const [prediction, setPrediction] = useState('');
  const [metrics, setMetrics] = useState(null);
  const [loading, setLoading] = useState(false);
  const [analyzing, setAnalyzing] = useState(false);
  const [problemType, setProblemType] = useState(null);
  const [availableAlgorithms, setAvailableAlgorithms] = useState([]);
  const [analyzed, setAnalyzed] = useState(false);
  const [showMetrics, setShowMetrics] = useState(false);
  const [showOverviewPreview, setShowOverviewPreview] = useState(false);
  const [rowCount, setRowCount] = useState(0);

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    setFile(selectedFile);
    setAnalyzed(false);
    setProblemType(null);
    setAlgorithm('');
    setPrediction('');
    setMetrics(null);
    setAvailableAlgorithms([]);

    // Read the CSV file to count the number of rows
    if (selectedFile && selectedFile.type === 'text/csv') {
      const reader = new FileReader();
      reader.onload = (event) => {
        const text = event.target.result;
        // Count rows by splitting by newline and excluding empty lines
        const rows = text.split('\n').filter(line => line.trim() !== '');
        // Assume the first row is the header, so subtract one if necessary.
        const count = rows.length > 0 ? rows.length - 1 : 0;
        setRowCount(count);
      };
      reader.readAsText(selectedFile);
    }
  };

  const handleAnalyze = async () => {
    if (!file) {
      alert('Please select a CSV file first!');
      return;
    }
    const formData = new FormData();
    formData.append('file', file);
    try {
      setAnalyzing(true);
      const analyzeRes = await fetch('http://localhost:5000/analyze', {
        method: 'POST',
        body: formData,
      });
      if (!analyzeRes.ok) {
        const error = await analyzeRes.json();
        throw new Error(error.error || 'Analysis failed');
      }
      const analyzeResult = await analyzeRes.json();
      if (analyzeResult.error) throw new Error(analyzeResult.error);

      setProblemType(analyzeResult.problem_type);
      setAnalyzed(true);
      // Removed random_forest from options.
      if (analyzeResult.problem_type === 'classification') {
        setAvailableAlgorithms([
          'logistic_regression',
          'knn',
          'naive_bayes',
          'decision_tree',
          'svm'
        ]);
      } else {
        setAvailableAlgorithms([
          'linear_regression',
          'decision_tree',
          'svm'
        ]);
      }
    } catch (err) {
      alert('Error: ' + err.message);
    } finally {
      setAnalyzing(false);
    }
  };

  const handleSubmit = async () => {
    if (!file || !algorithm) {
      alert('Please select a file and an algorithm!');
      return;
    }
    const formData = new FormData();
    formData.append('file', file);
    formData.append('algorithm', algorithm);
    formData.append('show_metrics', showMetrics ? 'true' : 'false');
    try {
      setLoading(true);
      const trainRes = await fetch('http://localhost:5000/train', {
        method: 'POST',
        body: formData,
      });
      if (!trainRes.ok) {
        const error = await trainRes.json();
        throw new Error(error.error || 'Training failed');
      }
      const trainResult = await trainRes.json();
      if (trainResult.error) throw new Error(trainResult.error);

      const queryPred = Array.isArray(trainResult.query_prediction)
        ? trainResult.query_prediction[0]
        : trainResult.query_prediction;
      setPrediction(queryPred);

      let metricsCopy = trainResult.metrics ? { ...trainResult.metrics } : null;
      let plotImage = null;

      if (metricsCopy && metricsCopy.plot) {
        plotImage = metricsCopy.plot;
        delete metricsCopy.plot;
      }
      setMetrics({ ...metricsCopy, plot: plotImage });
    } catch (err) {
      alert('Error: ' + err.message);
    } finally {
      setLoading(false);
    }
  };

  const theme = createTheme({
    palette: {
      primary: { main: '#1976d2' },
      secondary: { main: '#f50057' },
      success: { main: '#4caf50' }
    },
    typography: { fontFamily: 'Roboto, sans-serif' },
    components: {
      MuiButton: {
        styleOverrides: {
          root: {
            textTransform: 'none',
            fontWeight: 'bold',
            borderRadius: '12px',
            transition: 'background-color 0.3s, color 0.3s, box-shadow 0.3s, transform 0.3s',
            '&:hover': {
              backgroundColor: 'white',
