      <CssBaseline />
      <GlobalStyles
        styles={{
          '@keyframes fadeIn': {
            '0%': { opacity: 0 },
            '100%': { opacity: 1 }
          },
          '@keyframes slideDown': {
            '0%': { transform: 'translateY(-20px)', opacity: 0 },
