                  component="span"
                  aria-label="Upload CSV file"
                  sx={{
                    backgroundColor: '#1976d2',
                    color: 'white',
                    mr: 2,
                    px: 3,
                    py: 1,
                    borderRadius: '12px',
                    '--btn-bg': '#1976d2',
                    '&:hover': {
                      backgroundColor: 'white',
                      color: '#1976d2'
                    },
                    transition: 'all 0.3s'
                  }}
                  disabled={analyzing}
                >
                  Upload CSV
                </Button>
              </label>
              <Button
                variant="contained"
                onClick={handleAnalyze}
                sx={{
                  backgroundColor: 'orange',
                  color: 'white',
                  px: 3,
                  py: 1,
                  borderRadius: '12px',
                  '--btn-bg': 'orange',
                  '&:hover': {
                    backgroundColor: 'white',
                    color: 'orange'
                  },
                  transition: 'all 0.3s'
                }}
                disabled={!file || analyzing}
              >
                {analyzing ? <CircularProgress size={24} sx={{ color: 'orange' }} /> : 'Analyze'}
              </Button>
              {file && (
                <Typography sx={{ mt: 2, color: '#333', fontStyle: 'italic' }}>
                  Selected file: {file.name} (Rows: {rowCount})
                </Typography>
              )}
            </Box>
            {analyzed && problemType && (
              <>
                <FormControl fullWidth sx={{ mt: 3, animation: 'fadeIn 0.5s ease-out' }}>
                  <InputLabel sx={{ color: '#333' }}>Algorithm</InputLabel>
                  <Select
                    value={algorithm}
                    onChange={(e) => setAlgorithm(e.target.value)}
                    label="Algorithm"
                    sx={{ color: '#333' }}
                  >
                    {availableAlgorithms.map((alg) => (
                      <MenuItem key={alg} value={alg}>
                        {alg.replace(/_/g, ' ')}
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>
                <FormControlLabel
                  control={
                    <Checkbox
                      checked={showMetrics}
                      onChange={(e) => setShowMetrics(e.target.checked)}
                      color="secondary"
                    />
                  }
                  label="Show detailed metrics"
                  sx={{ mt: 2, color: '#333' }}
                />
              </>
            )}
            <Button
              variant="contained"
              fullWidth
              onClick={handleSubmit}
              sx={{
                mt: 4,
                backgroundColor: '#4caf50',
                color: 'white',
                borderRadius: '12px',
                '--btn-bg': '#4caf50',
                '&:hover': {
                  backgroundColor: 'white',
                  color: '#4caf50'
                },
                py: 1.5,
                fontSize: '1rem',
                transition: 'all 0.3s'
              }}
              disabled={loading || !analyzed || !algorithm}
            >
              {loading ? <CircularProgress size={24} sx={{ color: '#fff' }} /> : 'Train & Predict'}
            </Button>
          </Box>

          {/* Right Panel */}
          <Box
            sx={{
              flex: 1,
              background: 'linear-gradient(135deg, #7f39fb, #9c27b0)',
              p: 4,
              borderRadius: 2,
              minHeight: '500px',
              display: 'flex',
              flexDirection: 'column',
              justifyContent: 'flex-start',
              transition: 'transform 0.5s',
              '&:hover': { transform: 'scale(1.02)' },
              animation: 'slideDown 0.6s ease-out'
            }}
          >
            <Typography
              variant="h5"
              sx={{
                textAlign: 'center',
                fontWeight: 'bold',
                color: '#fff',
                mb: 3
              }}
            >
              Results
            </Typography>
            {prediction && (
              <Box
                sx={{
                  backgroundColor: 'rgba(255,255,255,0.85)',
                  borderRadius: 2,
                  p: 2,
                  mb: 3,
                  boxShadow: '0 0 10px rgba(0,0,0,0.1)',
                  textAlign: 'center',
                  animation: 'fadeIn 0.8s ease-out'
                }}
              >
                <Typography
                  variant="h6"
                  sx={{
                    color: '#6a1b9a',
                    fontWeight: 'bold'
                  }}
                >
                  Prediction: {prediction}
                </Typography>
              </Box>
            )}
            {showMetrics && metrics && (
              <Box
                sx={{
                  backgroundColor: 'rgba(255,255,255,0.85)',
                  borderRadius: 2,
                  p: 2,
                  boxShadow: '0 0 10px rgba(0,0,0,0.1)',
                  flex: '1 1 auto',
                  animation: 'fadeIn 0.8s ease-out'
                }}
              >
                <RenderMetrics
                  metrics={(() => {
                    const m = { ...metrics };
                    delete m.plot;
                    return m;
                  })()}
                />
              </Box>
            )}
          </Box>
        </Box>
      </Container>

      {/* Plot Rendering */}
{metrics && metrics.plot && (
  <Container maxWidth="lg" sx={{ py: 2 }}>
    <Box
      sx={{
        backgroundColor: 'rgba(255,255,255,0.9)',
        borderRadius: 2,
        p: 2,
        boxShadow: '0 0 10px rgba(0,0,0,0.1)',
        textAlign: 'center',
        animation: 'fadeIn 0.8s ease-out',
        overflowX: 'auto'
      }}
    >
      <Typography variant="h6" sx={{ mb: 2, fontWeight: 'bold', color: '#1976d2' }}>
        Model Visualization
      </Typography>
      <img
        src={`data:image/png;base64,${metrics.plot}`}
        alt="Model Visualization"
        style={{ 
          maxWidth: '100%',
          height: 'auto',
          borderRadius: '4px',
          minWidth: metrics.plot_width ? `${metrics.plot_width}px` : '100%',
          minHeight: metrics.plot_height ? `${metrics.plot_height}px` : '500px'
        }}
      />
    </Box>
  </Container>
)}
      )}
    </ThemeProvider>
  );
}

export default App;
